import { useState, useEffect } from "react";
import { CKEditor } from "@ckeditor/ckeditor5-react";
import { ClassicEditor } from "@ckeditor/ckeditor5-build-classic";

const TaskForm = ({ task, onCreateTask, onUpdateTask, users }) => {
  const [title, setTitle] = useState("");
  const [description, setDescription] = useState("");
  const [status, setStatus] = useState("");
  const [dueDate, setDueDate] = useState("");
  const [assignedTo, setAssignedTo] = useState("");

  useEffect(() => {
    if(task) {
      setTitle(task ? task.title : "");
      setDescription(task.description || "");
      setStatus(task.status || "To-Do");
      setDueDate(task.dueDate ? task.dueDate.split("T")[0] : "");
      setAssignedTo(task.assignedToId || "");
    } else {
      setTitle("");
      setDescription("");
      setStatus("To-Do");
      setDueDate("");
      setAssignedTo("");
    }
  }, [task]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!title.trim()) return;

    const taskData = { 
      title, 
      description, 
      status, 
      dueDate, 
      assignedToId: assignedTo ? parseInt(assignedTo) : null,
    };

    if (task) {
      await onUpdateTask({ ...task, ...taskData });
    } else {
      await onCreateTask(taskData);
    }

    // Reset form
    setTitle("");
    setDescription("");
    setStatus("To-Do");
    setDueDate("");
    setAssignedTo("");
  };

  const handleImageUpload = async (event) => {
    const file = event.target.files[0];
    if (!file) return;
  
    const formData = new FormData();
    formData.append("file", file);
  
    try {
      const response = await fetch("/api/tasks/upload", {
        method: "POST",
        body: formData,
      });
      const data = await response.json();
  
      if (data.url) {
        setDescription((prev) => prev + `<img src="${data.url}" alt="Uploaded Image" />`);
      }
    } catch (error) {
      console.error("Upload failed", error);
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <input type="text" value={title} onChange={(e) => setTitle(e.target.value)} required />
      <CKEditor
        editor={ClassicEditor}
        data={description}
        onChange={(_, editor) => setDescription(editor.getData())}
        config={{
          ckfinder: {
            uploadUrl: "/api/upload" 
          }
        }}
      />
      <input type="file" accept="image/*" onChange={handleImageUpload} />
      <select value={status} onChange={(e) => setStatus(e.target.value)}>
        <option value="To-Do">To-Do</option>
        <option value="In Progress">In Progress</option>
        <option value="Done">Done</option>
      </select>
      <input type="date" value={dueDate} onChange={(e) => setDueDate(e.target.value)} required/>
      <select value={assignedTo} onChange={(e) => setAssignedTo(e.target.value)} required>
        <option value="">Chọn người phụ trách</option>
        {users.map((user) => (
          <option key={user.id} value={user.id}>
            {user.name}
          </option>
        ))}
      </select>
      <button type="submit">{task ? "Cập nhật" : "Thêm"}</button>
      {task && <button type="button" onClick={() => onUpdateTask(null)}>Hủy</button>}
    </form>
  );
};

export default TaskForm;
