import { useState, useEffect } from "react";

const TaskForm = ({ task, onCreateTask, onUpdateTask, users, currentUser }) => {
  const [title, setTitle] = useState("");
  const [description, setDescription] = useState("");
  const [status, setStatus] = useState("To-Do");
  const [dueDate, setDueDate] = useState("");
  const [assignedTo, setAssignedTo] = useState("");

  useEffect(() => {
    if(task) {
      setTitle(task ? task.title : "");
      setDescription(task.description || "");
      setStatus(task.status || "To-Do");
      setDueDate(task.dueDate ? task.dueDate.split("T")[0] : "");
      setAssignedTo(task.assignedToId || "");
    } else {
      setTitle("");
      setDescription("");
      setStatus("To-Do");
      setDueDate("");
      setAssignedTo("");
    }
  }, [task]);

  const canEdit = currentUser.id === task?.assignedToId || currentUser.id === task?.createdById;
  useEffect(() => {
    if (!task || status === "Done") return;
    
    const now = new Date();
    const dueDateObj = new Date(dueDate);

    if (dueDateObj < now && status !== "Overdue") {
      setStatus("Overdue");
    }
  }, [dueDate, status, task]);

  const handleStatusChange = (newStatus) => {
    if (!canEdit) {
      alert("Bạn không có quyền chỉnh sửa trạng thái task này!");
      return;
    }

    if (status === "Overdue" && newStatus === "To-Do") {
      alert("Không thể chuyển từ Overdue về To-Do!");
      return;
    }

    if (status === "To-Do" && newStatus === "Done") {
      alert("Cần chuyển sang In Progress trước khi hoàn thành task!");
      return;
    }

    setStatus(newStatus);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!title.trim()) return;

    const taskData = { 
      title, 
      description, 
      status, 
      dueDate, 
      assignedToId: assignedTo ? parseInt(assignedTo) : null,
    };

    if (task) {
      await onUpdateTask({ ...task, ...taskData });
    } else {
      await onCreateTask(taskData);
    }

    // Reset form
    setTitle("");
    setDescription("");
    setStatus("To-Do");
    setDueDate("");
    setAssignedTo("");
  };

  return (
    <form onSubmit={handleSubmit}>
      <input type="text" value={title} onChange={(e) => setTitle(e.target.value)} required />
      <input value={description} onChange={(e) => setDescription(e.target.value)} placeholder="Description" />
      {canEdit ? (
        <select value={status} onChange={(e) => handleStatusChange(e.target.value)}>
          <option value="To-Do">To-Do</option>
          <option value="In Progress">In Progress</option>
          <option value="Done">Done</option>
          <option value="Overdue" disabled>Overdue</option>
        </select>
      ) : (
        <p>Trạng thái: {status}</p>
      )}
      <input type="date" value={dueDate} onChange={(e) => setDueDate(e.target.value)} required/>
      <select value={assignedTo} onChange={(e) => setAssignedTo(e.target.value)} required>
        <option value="">Chọn người phụ trách</option>
        {users.map((user) => (
          <option key={user.id} value={user.id}>
            {user.name}
          </option>
        ))}
      </select>
      <button type="submit">{task ? "Cập nhật" : "Thêm"}</button>
      {task && <button type="button" onClick={() => onUpdateTask(null)}>Hủy</button>}
    </form>
  );
};

export default TaskForm;
